/** 
 * Client-side chat window connection, adapted for use from agario-clone (https://github.com/huytd/agar.io-clone/) by Ben Cuan
 * Created 17 April 2018
 */

import {GLOBAL} from '../global.js';
import { socket } from '../socket.js';
import {devTest} from '../app.js';

let player, room, team;
export default class ChatClient {

    // Use this constructor during init to connect ChatClient to the server
    constructor(params) {
        // this.canvas = params.canvas;
        // this.mobile = params.mobile;
        this.player = params.player;
        this.team = params.team;
        const self = this;
        this.commands = {};
        this.commandPrefix = "-";
        let input = document.getElementById('chatInput');
        input.addEventListener('keypress', key => {this.sendChat(key);}); //This works WTF
        input.addEventListener('keyup', key => {
            input = document.getElementById('chatInput');
            key = key.which || key.keyCode;
            if (key === global.KEY_ESC) {
                input.value = '';
                // self.canvas.cv.focus();
            }
        });
    }

    // TODO: Break out many of these GameControls into separate classes.
    /** 
    * MISSING COMMENT
    */
    registerFunctions() {
        const self = this;

        this.registerCommand('help', 'Information about the chat commands.', () => {
            self.printHelp();
        });

        this.registerCommand('test', 'Gives 5000 of every element', () => {
            if(GLOBAL.DEBUG) {
                devTest();
                self.addSystemLine("Developer Configurations Applied!");
            }
            else {
                self.addSystemLine("Invalid Permission.");
            }
        });
        this.registerCommand('damage', 'Damages you by the given amount.', (params) => {
            console.log(params);
            if(GLOBAL.DEBUG) {
                if(params[0] !== undefined && typeof parseInt(params[0]) === 'number') {
                    socket.emit('damage', {damage: parseInt(params[0]), sender: socket.id, player: socket.id});
                    self.addSystemLine("Damaged player by " + params[0] + " health points");
                }
                else
                    self.addSystemLine("Invalid parameter. Parameter must be of type number");
            }
            else {
                self.addSystemLine("Invalid Permission.");
            }
        });
        this.registerCommand('spawnAtom', 'Spawns a Hydrogen atom at the bottom left vent.', () => {
            socket.emit('spawnAtom', {row: 9, col: 0});
        })

        // this.registerCommand('login', 'Login as an admin.', function (args) {
        //     self.socket.emit('pass', args);
        // });

        // this.registerCommand('kick', 'Kick a player, for admins only.', function (args) {
        //     self.socket.emit('kick', args);
        // });
        global.chatClient = this;
    }

    /**
     * Places the message DOM node into the chat box.
     * @param {string} innerHTML The message to be displayed.
     * @param {string} styleClass How the message should be styled - see `main.css` for styles and to create more styles.
     */
    appendMessage(innerHTML, styleClass) {
        if (this.mobile)
            return;

        const newline = document.createElement('li');

        // Colours the chat input correctly.
        newline.className = styleClass;
        // Add content
        newline.innerHTML = innerHTML;

        const chatList = document.getElementById('chatList');
        // Remove old chats
        if (chatList.childNodes.length > GLOBAL.MAX_CHATS) {
            chatList.removeChild(chatList.childNodes[0]);
        }
        chatList.appendChild(newline);
        //Scroll to view new chat
        chatList.scrollTop += 100;
    }

    /**
     * Chat box implementation for the users.
     * @param {string} name Name of the player who sent the message
     * @param {string} message Message that was sent
     * @param {boolean} me True if the sender matches the receiver
     */
    addChatLine(name, message, me) {
       this.appendMessage(
           `<b>${(name.length < 1) ? GLOBAL.PLACEHOLDER_NAME : name}</b>: ${message}`,
           (me) ? 'me' : 'friend'
        );
    }

    /**
     * Chat box implementation for the users.
     * @param {string} name Name of the player who sent the message
     * @param {string} message Message that was sent
     * @param {boolean} me True if the sender matches the receiver
     */
    addPrivateMessage(name, message, me) {
       this.appendMessage(
           `<b>${(name.length < 1) ? GLOBAL.PLACEHOLDER_NAME : name}</b>: ${message}`,
           (me) ? 'me' : 'friend'
        );
    }

    // Message to notify players when a new player joins
    addLoginMessage(name, me) {
        console.log(`${name} joined`);

        this.appendMessage(
            `<b>${(me) ? '</b>You have' : (name.length < 1) ? GLOBAL.PLACEHOLDER_NAME : name + '</b> has'} joined the room!`,
            'join'
        );
    }

    // Chat box implementation for the system.
    addSystemLine(message) {
        this.appendMessage(
            message,
            'system'
        );
    }

    // Places the message DOM node into the chat box.
    // appendMessage(node) {
    //     if (this.mobile) {
    //         return;
    //     }
        // const chatList = document.getElementById('chatList');
        // // if (chatList.childNodes.length > 10) {
        // //     chatList.removeChild(chatList.childNodes[0]);
        // // }
        // chatList.appendChild(node);
    // }

    // Sends a message or executes a command on the click of enter.
    sendChat(key) {
        const commands = this.commands;
        const input = document.getElementById('chatInput');

        key = key.which || key.keyCode;

        if (key === GLOBAL.KEY_ENTER) {
            const text = input.value.replace(/(<([^>]+)>)/ig, '');
            if (text !== '') {

                // Chat command.
                if (text.indexOf(this.commandPrefix) === 0) {
                    const args = text.substring(1).split(' ');
                    if (commands[args[0]]) {
                        commands[args[0]].callback(args.slice(1));
                    } else {
                        this.addSystemLine(`Unrecognized Command: ${text}, type -help for more info.`);
                    }

                    // Allows for regular messages to be sent to the server.
                } else {
                    //Debug lines for messages - Remove on production
                    // console.log("This Player: " + this.player);
                    // console.log("This message: " + text);
                    socket.emit('playerChat', { sender: this.player, message: text });
                    this.addChatLine(this.player, text, true);
                }

                // Resets input.
                input.value = '';
                // this.canvas.cv.focus();
            }
        }
    }

    // Allows for addition of commands.
    registerCommand(name, description, callback) {
        this.commands[name] = {
            description,
            callback
        };
    }

    // Allows help to print the list of all the commands and their descriptions.
    printHelp() {
        const commands = this.commands;
        for (const cmd in commands) {
            if (commands.hasOwnProperty(cmd)) {
                this.addSystemLine(`-${cmd}: ${commands[cmd].description}`);
            }
        }
    }
}

/**
 * Cookies.js was adopted from a StackOverflow answer 
 * (https://stackoverflow.com/questions/14573223/set-cookie-and-get-cookie-with-javascript).
 */

/**
 * Sets the value of a cookie.
 * @param {string} name Name of cookie
 * @param {string} value New value of cookie
 * @param {number} days Number of days this cookie will last for
 */
export function setCookie(name, value, days) {
    let expires = "";
    if (days) {
        const date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        expires = `; expires=${date.toUTCString()}`;
    }
    document.cookie = `${name}=${value || ""}${expires}; path=/`;
}

/**
 * Sets the value of a cookie.
 * @param {string} name Name of cookie
 * @return {string} The value of the cookie. Returns null if the cookie is not found.
 */
export function getCookie(name) {
    const nameEQ = `${name}=`;
    const ca = document.cookie.split(';');

    for (let c of ca) {
        while (c.charAt(0) == ' ') c = c.substring(1, c.length);
        if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
    }

    return null;
}

/**
 * Removes the given cookie.
 * @param {string} name The name of the cookie to erase.
 */
export function eraseCookie(name) {
    document.cookie = `${name}=; Max-Age=-99999999;`;
}

/**
 * Key listener function, adapted from https://github.com/kittykatattack/learningPixi#keyboard
 * Please refer to this link for extended documentation.
 * @param {number} keyCode ASCII key code for the key to listen. For best results declare the key codes in GLOBAL.js 
 */
import { isFocused } from '../pixigame';


export function keyboard(keyCode) {
  let key = {};
  key.code = keyCode;
  key.isDown = false;
  key.isUp = true;
  key.press = undefined;
  key.release = undefined;
  //The `downHandler`
  key.downHandler = event => {
      if (event.keyCode === key.code) {
        if (isFocused()) {
          if (key.isUp && key.press){
            key.press();
          }
          key.isDown = true;
          key.isUp = false;
        }
        //If 
        else{
          key.isDown = false;
          key.isUp = true;
        }
    // event.preventDefault();
      }
  };

  //The `upHandler`
  key.upHandler = event => {
      if (event.keyCode === key.code) {
        if(isFocused()){
          if (key.isDown && key.release){
            key.release();
          }
          key.isDown = false;
          key.isUp = true;
        }
        else{
          key.isDown = false;
          key.isUp = true;
        }
      }
    // event.preventDefault();
  };

  //Attach event listeners
  window.addEventListener(
    "keydown", key.downHandler.bind(key), false
  );
  window.addEventListener(
    "keyup", key.upHandler.bind(key), false
  );
  return key;
}

import * as PIXI from 'pixi.js';
import { Player } from './player.js';
import { GLOBAL } from '../global.js';
import { GameObject } from './gameobject.js';
import { distanceBetween } from '../global.js';
import { screenCenterX, screenCenterY, player } from '../pixigame.js';
import { socket } from '../socket.js';
import { updateAtomList } from '../app.js';

/**
 * An Atom can be picked up by players and used to create Compounds.
 */
export class Atom extends GameObject {

    /**
     * Creates a atom at the given coordinates. If no coordinates are given, then 
     * the atom spawns in a random location.
     * @param {number} id The ID of this atom (generated by server)
     * @param {string} typeID The numerical type of this atom (see spawnAtom or global for details)
     * @param {string} texture The Pixi texture corresponding to this Atom
     * @param {number} x (optional) X Coordinate of the atom 
     * @param {number} y (optional) Y Coordinate of the atom 
     * @param {number} vx Horizontal velocity
     * @param {number} vy Vertical velocity
     */
    constructor(id, typeID, texture, x, y, vx, vy) {
        // Atoms have a random ID between 100000 and 999999, inclusive.
        super(texture, id, x, y, vx, vy);
        this.height = GLOBAL.ATOM_RADIUS * 2;
        this.width = GLOBAL.ATOM_RADIUS * 2;
        this.typeID = typeID;
    }

    /**
     * Run when players are nearby to check if they picked this atom up.
     * If the player is nearby but not close enough to pick up, then it becomes attracted towards the player.
     * @returns true if collision detected, false otherwise
     */
    checkCollision() {
        if ( player === undefined)
