/**
 * Changes the health of the player by the amount given.
 * @param {*} data The data sent by the client. Contains:
 *  - damage (number)
 *  - player (id string of player that was hit)
 *  - id (id string of compound)
 *  - sentBy (id string of player that sent compound)
 * @param {string} room This room.
 * @param {*} socket This socket.
 * Must include the player id and amount to damage.
 * Amount may be negative (for health boost).
 */
export function damage(data, room, socket) {

    let thisRoom = getField(['rooms', room]);
    let thisPlayer = thisRoom.players[data.player];

    if (thisPlayer !== undefined) {

        // thisPlayer.health -= data.damage;
        setField(thisPlayer.health - data.damage, ['rooms', room, 'players', data.player, 'health']);

        // Add damage to database
        if (thisPlayer.damagedBy[data.sentBy] === undefined)
            setField(0, ['rooms', room, 'players', data.player, 'damagedBy', data.sentBy]);
        // thisPlayer.damagedBy[data.sentBy] += data.damage;
        setField(thisPlayer.damagedBy[data.sentBy] + data.damage, ['rooms', room, 'players', data.player, 'damagedBy', data.sentBy]);

        if (thisPlayer.health <= 0) {
            // console.log(thisRoom.teams.indexOf(socket.handshake.query.team));
            socket.emit('serverSendPlayerDeath', { teamNumber: getTeamNumber(room, socket.handshake.query.team) });
            setField(GLOBAL.MAX_HEALTH, ['rooms', room, 'players', data.player, 'health']);

            if(data.id !== undefined) {
                // Read damagedBy to award points, clear in the process
                let max = null;
                let dataToSend;
                for (let pl in thisPlayer.damagedBy) {
                    dataToSend = {
                        player: pl,
                        teamSlot: getTeamNumber(room, thisRoom.compounds[data.id].sendingTeam),
                        increment: GLOBAL.ASSIST_SCORE,
                        kill: false
                    };

                    // Add to team score, checking if team score is initialized
                    setField((thisRoom.teams[dataToSend.teamSlot].score === undefined) ? dataToSend.increment : thisRoom.teams[dataToSend.teamSlot].score + dataToSend.increment, ['rooms', room, 'teams', dataToSend.teamSlot, 'score']);


                    socket.to(room).broadcast.emit('serverSendScoreUpdate', dataToSend);
                    socket.emit('serverSendScoreUpdate', dataToSend);
                    if (max === null || thisPlayer.damagedBy[pl] > thisPlayer.damagedBy[max])
                        max = pl;
                }

                // Add to score of person who dealt the most damage
                dataToSend.player = max;
                dataToSend.increment = GLOBAL.KILL_SCORE - GLOBAL.ASSIST_SCORE;
                dataToSend.kill = true;
                socket.to(room).broadcast.emit('serverSendScoreUpdate', dataToSend);
                socket.emit('serverSendScoreUpdate', dataToSend);

                // Add to team score
                setField(thisRoom.teams[dataToSend.teamSlot].score + dataToSend.increment, ['rooms', room, 'teams', dataToSend.teamSlot, 'score']);

                // Clear damagedBy values
                for (let pl in thisPlayer.damagedBy)
                    setField(0, ['rooms', room, 'players', data.player, 'damagedBy', pl]);

                // Check if a team won
                for (let tm of thisRoom.teams) {
                    if (tm.score >= GLOBAL.WINNING_SCORE) {

                        let dataToSend = {
                            winner: tm
                            // teamScore: thisRoom.teams[dataToSend.teamSlot].score             
                            //other data here TODO post ranking
                        };
                        socket.to(room).broadcast.emit('serverSendWinner', dataToSend);
                        socket.emit('serverSendWinner', dataToSend);

                        // Close room after delay (kick all players)
                        setTimeout(() => {
                            socket.emit('serverSendDisconnect', {});
                            socket.to(room).broadcast.emit('serverSendDisconnect', {});
                        }, GLOBAL.ROOM_DELETE_DELAY);
                    }
                }
            }
        }
    }
    else
        console.warn('Player of ID ' + data.player + ' couldn\'t be damaged because they don\'t exist!');
}

import { getField, setField } from '../server';
import { MAP_LAYOUT, TILES, TILE_NAMES } from '../../client/js/obj/tiles';
import { GLOBAL } from '../../client/js/global';
import { spawnAtomAtVent } from './serverutils';
import colors from 'colors'; // Console colors :D

/**
 * Methods to run on server initialization and player connect initialization.
 */

/**
 * Global initialiation. Run once on server start.
 */
export function initGlobal() {
    // Set up atom spawning three times a second. This is processed outside of the player specific behavior because more players joining !== more resources spawn.
    setInterval(() => {
        for (let room in getField(['rooms'])) {
            for (let row = 0; row < MAP_LAYOUT.length; row++)
                for (let col = 0; col < MAP_LAYOUT[0].length; col++) {
                    if (TILES[TILE_NAMES[MAP_LAYOUT[row][col]]].type === 'spawner') {
                        spawnAtomAtVent(row, col, room, false);
                    }

                }
        }
    }, GLOBAL.ATOM_SPAWN_DELAY);

    // Timer
    setInterval(() => {
        for (let room in getField(['rooms'])) {
            if (getField(['rooms', room, 'started'])) {
                let seconds = getField(['rooms', room, 'time', 'seconds']), 
                    minutes = getField(['rooms', room, 'time', 'minutes']);

                // Equivalent to rooms[room].time.seconds++;
                setField(seconds + 1, ['rooms', room, 'time', 'seconds']);

                if (seconds >= 60) {
                    setField(0, ['rooms', room, 'time', 'seconds']);
                    setField(minutes + 1, ['rooms', room, 'time', 'minutes']);
                }

                // Set formatted Time
                setField(minutes + ':' + ((seconds < 10) ? '0' : '') + seconds, ['rooms', room, 'time', 'formattedTime']);
            }
        }
    }, 1000);
}

/**
 * Run on every player join.
 * @param {*} socket The socket.io instance
 * @param {string} room The name of the room that the player belongs to
 */
export function initPlayer(socket, room) {
    // Initialize room array and spawn atoms on first player join
    let thisRoom = getField(['rooms', room]);
    if (thisRoom === undefined || thisRoom === null) {
        console.log('[Server] '.bold.blue + 'Setting up room '.yellow + ('' + room).bold.red + ' as type ' + socket.handshake.query.roomType);
        setField({
            joinable: true,
            teams: [],
            atoms: {},
            compounds: {},
            type: socket.handshake.query.roomType,
            time: {
                minutes: 0,
                seconds: 0,
                formattedTime: '0:00'
            }
        }, ['rooms', room]);
    }
    thisRoom = getField(['rooms', room]);

    // Add team to database

    // Equivalent to rooms[room].teams.push({ name: team });
    setField({name: socket.handshake.query.team}, ['rooms', room, 'teams', getField(['rooms', room, 'teams']).length]);

    // Check if room is full
    if (((thisRoom.type === '4v4' || thisRoom.type === '2v2') && thisRoom.teams.length === 2) || thisRoom.teams.length === 4)
        setField(false, ['rooms', room, 'joinable']);

    // Create new player in rooms object
    setField({
        id: socket.id,
        name: socket.handshake.query.name,
        room: socket.handshake.query.room,
        team: socket.handshake.query.team,
        health: GLOBAL.MAX_HEALTH,
        posX: GLOBAL.SPAWN_POINTS[thisRoom.teams.length - 1].x * GLOBAL.GRID_SPACING * 2,
        posY: GLOBAL.SPAWN_POINTS[thisRoom.teams.length - 1].y * GLOBAL.GRID_SPACING * 2,
        vx: 0,
        vy: 0,
        experience: 0,
        damagedBy: {}
    }, ['rooms', room, 'players', socket.id]);
}

import { TILES, TILE_NAMES, MAP_LAYOUT } from '../../client/js/obj/tiles';
import { getField, setField } from '../server';
import { GLOBAL } from '../../client/js/global';

/**
 * Misc. standalone utilities for the server.
 */

/**
* Returns a random number between between 10000000 and 99999999, inclusive.
* TODO Make every ID guaranteed unique
* @returns random id between 10000000 and 99999999
*/
export function generateID() {
    return Math.floor(Math.random() * 90000000) + 10000000;
}

/**
 * Spawns an atom at the vent at the given row and column.
 * @param {number} row The row of the vent 
 * @param {number} col The column of the vent to spawn at
 * @param {string} room The room to spawn in
 * @param {boolean} verbose True if this method should output to the console
 */
export function spawnAtomAtVent(row, col, room, verbose) {
    // Atom to spawn. Gets a random element from the tile paramter array `atomsToSpawn`
    let atomToSpawn = TILES[TILE_NAMES[MAP_LAYOUT[row][col]]].params.atomsToSpawn[Math.floor(Math.random() * TILES[TILE_NAMES[MAP_LAYOUT[row][col]]].params.atomsToSpawn.length)];

    let x = col * GLOBAL.GRID_SPACING * 2 + GLOBAL.GRID_SPACING;
    let y = row * GLOBAL.GRID_SPACING * 2 - GLOBAL.GRID_SPACING;

    spawnAtom(x, y, atomToSpawn, room, verbose);

}

/**
 * 
 * @param {number} x X-position of center
 * @param {number} y Y-position of center
 * @param {string} type Type of atom to spawn
 * @param {string} room The room to spawn in
 * @param {boolean} verbose True if this method should output to the console
 */
export function spawnAtom(x, y, type, room, verbose) {

    let theta = Math.random() * Math.PI * 2; // Set random direction for atom to go in once spawned

    let atom = {
        typeID: type,
        id: generateID(),
        posX: x,
        posY: y,
        vx: Math.cos(theta) * GLOBAL.ATOM_SPAWN_SPEED,
        vy: Math.sin(theta) * GLOBAL.ATOM_SPAWN_SPEED
    };
    if (getField(['rooms', room]) !== undefined)
        setField(atom, ['rooms', room, 'atoms', atom.id]);

    // Log to console
    if (verbose)
        console.log('SPAWN ATOM ' + atomToSpawn + ' theta:' + theta + ', vx: ' + atom.vx + ', vy: ' + atom.vy);
}

/**
 * Returns the index of the given team within the team array of the given room.
 * @param {string} room The room name to check
 * @param {string} teamName The team name to return the number of
 */
export function getTeamNumber(room, teamName) {
    for (let i = 0; i < getField(['rooms', room, 'teams']).length; i++)
        if (getField(['rooms', room, 'teams'])[i].name === teamName)
            return i;

    return -1; //Team not found
}
