import * as PIXI from 'pixi.js';
import { app, screenCenterX, screenCenterY, player } from '../pixigame';
import { GLOBAL } from '../global';
import { MAP_LAYOUT } from './tiles';

/**
 * GameObject class that all objects in the game space should inherit.
 * Provides standard field variables such as posX and posY, as well as 
 * standard methods to manipulate them.
 * 
 * A GameObject cannot be added directly; it must be inherited.
 * setup() and tick() must be overridden.
 */
export class GameObject extends PIXI.Sprite {

	/**
     * Creates a new GameObject.
     * @param {PIXI.Texture} texture The texture associated with this sprite
     * @param {string} id Unique identifier- for example, socket ID for players, numerical ID for atoms
     * @param {number} x Global x-coordinate
     * @param {number} y Global y-coordinate
     * @param {number} vx Horizontal velocity
     * @param {number} vy Vertical velocity
     */
	constructor(texture, id, x, y, vx, vy) {
		super(texture);
		this.id = id;
		this.posX = x;
		this.posY = y;
		this.vx = vx;
		this.vy = vy;
		this.destroyed = false;
        
		app.stage.addChild(this);
	}

	/**
     * Sets global coordinates of this player
     * @param {number} newX New x-coordinate to move to
     * @param {number} newY New y-coordinate to move to
     */
	setCoordinates(newX, newY) {
		this.posX = newX;
		this.posY = newY;
	}

	/**
     * Sets global coordinates and speeds of this player
     * @param {number} newX New x-coordinate to move to
     * @param {number} newY New y-coordinate to move to
     * @param {number} vx New x velocity
     * @param {number} vy New y velocity
     */
	setData(newX, newY, vx, vy) {
		this.setCoordinates(newX, newY);
		this.vx = vx;
		this.vy = vy;
	}

	/**
     * Call during tick() if necessary. 
     * Draws object in the correct position on the player screen.
     */
	draw() {
		if(player !== undefined && !this.destroyed) {
			this.x = screenCenterX + this.posX - player.posX;
			this.y = screenCenterY + player.posY - this.posY;
		}
	}

	/** TEMP
     * Moves this object to (9999, 9999) on local screen space, effectively
     * hiding it from view.
     */
	hide() {
		// console.warn('hide() is called');
		if (this.transform === null || this.transform === undefined) {
			console.warn('hide() function exception. THIS IS ABNORMAL. The following object contains invalid transform object:');
			console.warn(this);
			return 1;
		}
		this.x = 9999;
		this.y = 9999;
	}

	/**
     * Override optional. Called once, during game setup phase.
     */
	setup() {
	}

	/**
     * Override optional. Default behavior: handles movement. Call super.tick() from child class if movable.
     * @param {boolean} noDraw - true if tick() should only process movement, not drawing.
     */
	tick(noDraw) {
		// Prevent drifting due to minimal negative values
       
		if(this.destroyed)
			return;

		if (Math.abs(this.vx) < GLOBAL.DEADZONE)
			this.vx = 0;
		if (Math.abs(this.vy) < GLOBAL.DEADZONE)
			this.vy = 0;

		// Change position based on speed and direction. Don't allow objects to go out of bounds
		if ((this.vx > 0 && this.posX < MAP_LAYOUT[0].length * GLOBAL.GRID_SPACING * 2 - GLOBAL.GRID_SPACING) || (this.vx < 0 && this.posX > 0))
			this.posX += this.vx;
		if ((this.vy > 0 && this.posY < (MAP_LAYOUT.length - 1) * GLOBAL.GRID_SPACING * 2) || (this.vy < 0 && this.posY > -GLOBAL.GRID_SPACING))
			this.posY += this.vy;

		if (this.ignited)
			this.texture = PIXI.loader.resources[GLOBAL.IGNITE_SPRITE].texture;

		if(!noDraw) {
			this.draw();
		}
	}

	/**
     * Destroyes the Sprite
     */
	destroy() {
		this.destroyed = true;
		super.destroy();
	}

}

import * as PIXI from 'pixi.js';
import { TILES } from './tiles';
import { GLOBAL } from '../global';
import { app, player, screenCenterX, screenCenterY } from '../pixigame';
/**
 * One Tile component of the map. 
 */
export class MapTile extends PIXI.Sprite {

	/**
     * Constructs one Tile to add to the map.
     * @param {string} name Name of the tile to place. Takes this name from `tiles.js` and loads its resources
     * @param {number} gridX X-coordinate on the grid. Every 1 gridX = 400 posX 
     * @param {number} gridY Y-coordinate on the grid.
     */
	constructor(name, gridX, gridY) {
		super(PIXI.loader.resources[GLOBAL.TILE_TEXTURE_DIR + TILES[name].texture].texture);
		this.tile = TILES[name];
		this.posX = gridX * GLOBAL.GRID_SPACING * 2;
		this.posY = gridY * GLOBAL.GRID_SPACING * 2;
		this.height = GLOBAL.GRID_SPACING * 2;
		this.width = GLOBAL.GRID_SPACING * 2;
		app.stage.addChild(this);
	}

	/**
     * Draws object in the correct position on the player screen.
     */
	tick() {
		if (player !== undefined) {
			this.x = screenCenterX + this.posX - player.posX;
			this.y = screenCenterY + player.posY - this.posY;
		}
	}

	/** TEMP
     * Moves this tile to (9999, 9999) on local screen space, effectively
     * hiding it from view.
     */
	// hide() {
	//     this.x = 9999;
	//     this.y = 9999;
	// }

}

import {GLOBAL} from '../global.js';
import * as PIXI from 'pixi.js';
import { player, screenCenterX, screenCenterY } from '../pixigame.js';
import { socket } from '../socket.js';
import { GameObject } from './gameobject.js';
import { cookieInputs } from '../app.js';

export class Player extends GameObject {

	/**
     * Constructor for creating a new Player in the server side.
     * Player is a Sprite instance that can be added to the stage.
     * Each Player should only be created once, and updated subsequently with
     * setData().
     * @param {PIXI.Texture} texture The texture associated with this sprite
     * @param {string} id Socket ID of the player
     * @param {string} name Name of the player
     * @param {string} room Room that the player belongs to
     * @param {string} team Team that the player belongs to
     * @param {number} health Health of the player
     * @param {number} x Global x-coordinate
     * @param {number} y Global y-coordinate
     * @param {number} vx Horizontal velocity
     * @param {number} vy Vertical velocity
     */
	constructor(texture, id, name, room, team, health, x, y, vx, vy, experience) {

		// Call GameObject
		super(texture, id, x, y, vx, vy);

		// Pixi Values
		this.width = GLOBAL.PLAYER_RADIUS * 2;
		this.height = GLOBAL.PLAYER_RADIUS * 2;

		if(id === socket.id) {
			// console.log('this player');
			this.x = screenCenterX;
			this.y = screenCenterY;
		}
		else { // take this player off screen until it can be processed
			this.hide();
		}

		// Custom fields
		this.name = name;
		this.room = room;
		this.team = team;
		this.health = health; //Set the health of the player
		this.isMoving = false;
		this.experience = experience; //Sets the experience of the player(Passed in)
		this.speedMult = 1; // Speed multiplier. Increased/decreased by different compounds
		this.hasShield = false;

		this.damagedBy = {}; // Object containing the values of damage that each player has dealt.            
		this.textObjects = {}; // Contains Text to be drawn under the player (name, id, etc)

		this.setup();
	}

	/**
     * First-time setup for this player. All of the functions in this method will only be called once.
     */
	setup() {
		// Create text objects
		this.textObjects.nametext = new PIXI.Text('name: ');
		this.textObjects.idtext = new PIXI.Text('id: ');
		this.textObjects.postext = new PIXI.Text('placeholderpos');
		this.textObjects.teamtext = new PIXI.Text('team: ');
		this.textObjects.healthtext = new PIXI.Text('health: ');

		// Assign values and positions
		this.textObjects.idtext.position.set(0, GLOBAL.PLAYER_RADIUS * 9);
		this.textObjects.idtext.text += this.id;
		this.textObjects.nametext.position.set(0, GLOBAL.PLAYER_RADIUS * 9 + 100);
		this.textObjects.nametext.text += this.name;
		this.textObjects.postext.position.set(0, GLOBAL.PLAYER_RADIUS * 9 + 200);
		this.textObjects.teamtext.text += this.team;
		this.textObjects.teamtext.position.set(0, GLOBAL.PLAYER_RADIUS * 9 + 300);

		// create text and assign color
		for (let item in this.textObjects) {
			// Add text
			this.textObjects[item].style = new PIXI.TextStyle({
				fill: (cookieInputs[2].value === this.team) ? GLOBAL.FRIENDLY_COLOUR : GLOBAL.ENEMY_COLOUR,
				fontSize: 120
			});  
			this.addChild(this.textObjects[item]);
		}
	}
	/**
      * Draws all components of a given player.
      * This method should be included in the ticker and called once a frame.
    * Therefore, all setup tasks
     * should be called in setup().
    */
	tick() {
        
		// Movement
		super.tick(true);
        
		// Update text
		this.textObjects.postext.text = '(' + Math.round(this.posX) + ', ' + Math.round(this.posY) + ')';
		this.textObjects.healthtext.text = 'health: ' + this.health;
        

        

		// Draw other player
		if(this.id !== socket.id) {
			this.draw();
		}
	}

}

