import {incrementField, getField, setField } from '../server';
import { generateID } from './serverutils';
import { GLOBAL } from '../../client/js/global';
import { TILES, TILE_NAMES, MAP_LAYOUT } from '../../client/js/obj/tiles';

export function incrementAtom(player, room, atomType, quantity) {
	// console.log(atomType);
	incrementField(quantity, ['rooms', room, 'players', player, 'atomList', atomType]);
}


export function canCraft(player, room, blueprint) {
	// console.log(player);
	if (blueprint === undefined)
		return false;
	for (let atom in blueprint.atoms) {
		let numAtoms = getField(['rooms', room, 'players', player.id, 'atomList', atom]);
		if (numAtoms === undefined || numAtoms < blueprint.atoms[atom])
			return false;
	}

	return true;
}

/**
 * Spawns an atom at the vent at the given row and column.
 * @param {number} row The row of the vent 
 * @param {number} col The column of the vent to spawn at
 * @param {string} room The room to spawn in
 * @param {boolean} verbose True if this method should output to the console
 */
export function spawnAtomAtVent(row, col, room, verbose) {
	// Atom to spawn. Gets a random element from the tile paramter array `atomsToSpawn`
	let atomToSpawn = TILES[TILE_NAMES[MAP_LAYOUT[row][col]]].params.atomsToSpawn[Math.floor(Math.random() * TILES[TILE_NAMES[MAP_LAYOUT[row][col]]].params.atomsToSpawn.length)];

	let x = col * GLOBAL.GRID_SPACING * 2 + GLOBAL.GRID_SPACING;
	let y = row * GLOBAL.GRID_SPACING * 2 - GLOBAL.GRID_SPACING;

	spawnAtom(x, y, atomToSpawn, room, verbose);

}

/**
 * 
 * @param {number} x X-position of center
 * @param {number} y Y-position of center
 * @param {string} type Type of atom to spawn
 * @param {string} room The room to spawn in
 * @param {boolean} verbose True if this method should output to the console
 */
export function spawnAtom(x, y, type, room, verbose) {

	let theta = Math.random() * Math.PI * 2; // Set random direction for atom to go in once spawned

	let atom = {
		typeID: type,
		id: generateID(),
		posX: x,
		posY: y,
		vx: Math.cos(theta) * GLOBAL.ATOM_SPAWN_SPEED,
		vy: Math.sin(theta) * GLOBAL.ATOM_SPAWN_SPEED
	};
	if (getField(['rooms', room]) !== undefined)
		setField(atom, ['rooms', room, 'atoms', atom.id]);

	// Log to console
	if (verbose)
		console.log('SPAWN ATOM ' + atomToSpawn + ' theta:' + theta + ', vx: ' + atom.vx + ', vy: ' + atom.vy);
}

import { distanceBetween, GLOBAL } from '../../client/js/global';
import { setField, deleteObject } from '../server';
import { damage } from './ondamage';
import { incrementAtom } from './atoms';

/**
 * Runs once a frame, checks for collisions between objects and handles them accordingly.
 * Run using 
 * @param {*} socket The socket.io instance
 * @param {string} room The name of the room
 * @param {*} thisPlayer The player object
 * @param {*} tempObjects The list of objects to tick. Should only be the objects rendered on the screen of thisPlayer. Contains compounds, atoms, players
 */
export function collisionDetect(socket, room, thisPlayer, tempObjects) {

	// Check for collected atoms
	for (let atom in tempObjects.atoms) {
		let distance = distanceBetween(
			{ posX: tempObjects.atoms[atom].posX + GLOBAL.ATOM_RADIUS, posY: tempObjects.atoms[atom].posY - GLOBAL.ATOM_RADIUS },
			{ posX: thisPlayer.posX + GLOBAL.PLAYER_RADIUS, posY: thisPlayer.posY - GLOBAL.PLAYER_RADIUS });

		if (distance < GLOBAL.ATOM_COLLECT_THRESHOLD) {

			// console.log(atom);
			incrementAtom(thisPlayer.id, room, tempObjects.atoms[atom].typeID, 1);
			socket.to(room).broadcast.emit('serverSendObjectRemoval', { id: atom, type: 'atoms' });
			socket.emit('serverSendObjectRemoval', { id: atom, type: 'atoms' });
			deleteObject('atoms', atom, room, socket);

		}
	}

	// Check for compound collisions
	for (let compound in tempObjects.compounds) {

		let cmp = tempObjects.compounds[compound];

		if (cmp.sendingTeam !== thisPlayer.team) {
			let distance = distanceBetween(
				{ posX: cmp.posX + cmp.blueprint.params.size / 2, posY: cmp.posY - cmp.blueprint.params.size / 2 },
				{ posX: thisPlayer.posX + GLOBAL.PLAYER_RADIUS, posY: thisPlayer.posY - GLOBAL.PLAYER_RADIUS });

			// Hit player
			if (distance < cmp.blueprint.params.size + GLOBAL.PLAYER_RADIUS) {
				damage({
					damage: (cmp.ignited) ? cmp.blueprints.params.ignitedDamage : cmp.blueprint.params.damage,
					player: socket.id,
					sentBy: cmp.sender,
					id: compound
				}, room, socket);

				deleteObject('compounds', compound, room, socket);
			}
		}
	}
}

/**
 * Manages compound creation and behavior.
 * Moved from clientside compound.js
 */
import { GLOBAL, getCurrTile } from '../../client/js/global';
import { canCraft } from './atoms';
import { generateID } from './serverutils';
import { incrementField, setField, deleteObject, getField } from '../server';
import { damage } from './ondamage';
import { BLUEPRINTS } from '../../client/js/obj/blueprints';
import { addExperience } from './experience';

/**
  * Checks if a compound can be created, deducts craft material, and returns the new compound.
  * Does NOT add the new compound to the objects array.
  * @param data - Data sent from the client.
  *  - blueprint: The blueprint of the compound to create
  *  - sendingTeam: Team of the player who sent this
  *  - mousePos: Mouse position of the player who sent this. Contains x and y components
  *  - sender: Player who sent this
  *  - streamID: number indicating the consecutive compounds requested on this current mouse hold.
  * @param {string} room - The name of the room
  * @param socket - Socket.io instance
  */
export function createCompound(data, room, thisPlayer, socket) {


	if (!canCraft(thisPlayer, room, data.blueprint))
		return false;

	// Calculate velocities based on cursor position
	let theta = Math.atan2(data.mousePos.y, data.mousePos.x);
	let newCompound = {
		id: generateID(),
		posX: thisPlayer.posX + GLOBAL.PLAYER_RADIUS,
		posY: thisPlayer.posY - GLOBAL.PLAYER_RADIUS,
		vx: thisPlayer.vx + data.blueprint.params.speed * Math.cos(theta),
		vy: thisPlayer.vy + data.blueprint.params.speed * Math.sin(theta),
		blueprint: data.blueprint,
		sendingTeam: data.sendingTeam,
		sender: data.sender
	};
	// console.log("This player: ");
	// console.log(thisPlayer);
	// Add functionality for specific blueprint types
	if (data.blueprint.type === 'speed') {
		incrementField(data.blueprint.params.speedFactor * (1 / thisPlayer.speedMult), ['rooms', room, 'players', thisPlayer.id, 'speedMult']);
	}
	else if(data.blueprint.type === 'health') {
		damage({
			damage: -blueprint.params.healthModifier,
			sender: socket.id
		}, room, socket);
		if (thisPlayer.health > GLOBAL.MAX_HEALTH) {
			setField(GLOBAL.MAX_HEALTH, ['rooms', room, 'players', thisPlayer.id, 'health']);
		}
	}
	else if(data.blueprint.type === 'defense'){
		setField(true, ['rooms', room, 'players', thisPlayer.id, 'hasShield']);
	}

	//Emits the crafting event to update experience
	addExperience('CRAFT', socket, room, thisPlayer.id);

	// Remove atoms from inventory
	if (!data.streamID || data.streamID % data.blueprint.params.compoundsPerCraft === 0 || data.streamID === 1) {
		for (let atom in data.blueprint.atoms) {
			incrementField(-data.blueprint.atoms[atom], ['rooms', room, 'players', thisPlayer.id, 'atomList', atom]);
		}
	}

	return newCompound;
}


/**
 * Checks compound behavior based on compound type. Runs once a frame.
 * @param {number} compound compound object
 * @param {string} room Name of room
 */
export function tickCompound(compound, room, socket) {
	//TODO
	switch (compound.blueprint.type) {
	case 'flammable':
		if (getCurrTile(compound) === 'F' && !compound.ignited) {
			setField(true, ['rooms', room, 'compounds', compound.id, 'ignited']);
			// compound.ignited = true;
			// compound.texture = PIXI.loader.resources[GLOBAL.IGNITE_SPRITE].texture;
		}
		break;
	}

	if(compound.blueprint.params.evaporate) {
		if(getCurrTile(compound) === 'F') {
			deleteObject('compounds', compound.id, room, socket);
		}
	}
}

