/**
 * Adds experience to a given player.
 * @param {string} event The name of the event as specified in GLOBAL.EXPERIENCE_VALUES
 * @param {*} socket The socket instance
 * @param {string} room The ID of the room
 * @param {string} player ID of the player
 */
export function addExperience(event, socket, room, player) {
	//Add a specific amount to the players experience
	//Get the index of the Event and then pass it into the values array to get the actual value
	incrementField(GLOBAL.EXPERIENCE_VALUES[event], ['rooms', room, 'players', player, 'experience']);
	//  thisPlayer.experience += GLOBAL.EXPERIENCE_VALUES[data.event];

	// Determine the player's level based on experience
	let oldLevel = getField(['rooms', room, 'players', player, 'level']);
	for (let level of GLOBAL.EXPERIENCE_LEVELS) {
		if (getField(['rooms', room, 'players', player, 'level']) >= level)
			setField(GLOBAL.EXPERIENCE_LEVELS.indexOf(level) + 1, ['rooms', room, 'players', player, 'level']);
	}

	// Check to see if the player leveled up
	if (getField(['rooms', room, 'players', player, 'level']) > oldLevel) {
		socket.emit('levelUp', { newLevel: thisPlayer.level });
	}
}

import { distanceBetween, isInBounds, GLOBAL } from '../../client/js/global';
import { deleteObject, getField, setField } from '../server';
import { collisionDetect } from './collision';
import { tickCompound } from './compound';

/**
 * Runs once a frame, per player.
 * @param {*} socket The socket.io instance
 * @param {string} room The name of the room
 * @param {*} thisPlayer The player object
 */
export function frameSync(socket, room, thisPlayer) {
	if(socket.connected) {

		let thisRoom = getField(['rooms', room]);
    
		if (thisRoom !== undefined) {
			// Distance checking for all objects
			let tempObjects = {
				players: {},
				atoms: {},
				compounds: {}
			};
    
			// Move compounds
			for (let compound in thisRoom.compounds) {
				let compoundRef = thisRoom.compounds[compound];
				if (isInBounds(compoundRef)) {
					setField(compoundRef.posX + compoundRef.vx, ['rooms', room, 'compounds', compound, 'posX']);
					setField(compoundRef.posY + compoundRef.vy, ['rooms', room, 'compounds', compound, 'posY']);
					tickCompound(getField(['rooms', room, 'compounds', compound]), room, socket);
					// compoundRef.posX += compoundRef.vx;
					// compoundRef.posY += compoundRef.vy;
				}
				else { // delete
					deleteObject('compounds', compound, room, socket);
				}
			}
			// Move atoms
			for (let atom in thisRoom.atoms) {
				let distance = distanceBetween(
					{ posX: thisRoom.atoms[atom].posX + GLOBAL.ATOM_RADIUS, posY: thisRoom.atoms[atom].posY - GLOBAL.ATOM_RADIUS },
					{ posX: thisPlayer.posX + GLOBAL.PLAYER_RADIUS, posY: thisPlayer.posY - GLOBAL.PLAYER_RADIUS });
				// Attractive force
				if (distance < GLOBAL.ATTRACTION_RADIUS) {
					let theta = Math.atan2((thisPlayer.posY - thisRoom.atoms[atom].posY), (thisPlayer.posX - thisRoom.atoms[atom].posX));
    
					// Attraction is based on logarithmic algorithm
    
					setField(1 / distance * Math.cos(theta) * GLOBAL.ATTRACTION_COEFFICIENT, ['rooms', room, 'atoms', atom, 'vx']);
					setField(1 / distance * Math.sin(theta) * GLOBAL.ATTRACTION_COEFFICIENT, ['rooms', room, 'atoms', atom, 'vy']);
					// thisRoom.atoms[atom].vx = 1 / distance * Math.cos(theta) * GLOBAL.ATTRACTION_COEFFICIENT;
					// thisRoom.atoms[atom].vy = 1 / distance * Math.sin(theta) * GLOBAL.ATTRACTION_COEFFICIENT;
					// console.log(this.vx, this.vy, this.posX, this.posY);
				}
				// Atom slowing down
				else if (Math.abs(thisRoom.atoms[atom].vx) > GLOBAL.DEADZONE || Math.abs(thisRoom.atoms[atom].vy) > GLOBAL.DEADZONE) {
					setField(thisRoom.atoms[atom].vx * GLOBAL.VELOCITY_STEP, ['rooms', room, 'atoms', atom, 'vx']);
					setField(thisRoom.atoms[atom].vy * GLOBAL.VELOCITY_STEP, ['rooms', room, 'atoms', atom, 'vy']);
					// thisRoom.atoms[atom].vx *= GLOBAL.VELOCITY_STEP;
					// thisRoom.atoms[atom].vy *= GLOBAL.VELOCITY_STEP;
				}
    
				if (Math.abs(thisRoom.atoms[atom].vx) <= GLOBAL.DEADZONE)
					setField(0, ['rooms', room, 'atoms', atom, 'vx']);
				if (Math.abs(thisRoom.atoms[atom].vy) <= GLOBAL.DEADZONE)
					setField(0, ['rooms', room, 'atoms', atom, 'vy']);
    
				// Move atom
				setField(thisRoom.atoms[atom].posX + thisRoom.atoms[atom].vx, ['rooms', room, 'atoms', atom, 'posX']);
				setField(thisRoom.atoms[atom].posY + thisRoom.atoms[atom].vy, ['rooms', room, 'atoms', atom, 'posY']);
				// thisRoom.atoms[atom].posX += thisRoom.atoms[atom].vx;
				// thisRoom.atoms[atom].posY += thisRoom.atoms[atom].vy;
			}
            
			// Populate tempObjects
			for (let objType in tempObjects) {
				for (let obj in thisRoom[objType]) {
					if (distanceBetween(thisRoom[objType][obj], thisPlayer) < GLOBAL.DRAW_RADIUS)
						tempObjects[objType][obj] = thisRoom[objType][obj];
					else if (objType === 'players') // Player left view
						socket.emit('serverSendObjectRemoval', { id: obj, type: objType });
				}
			}

			// Run collision detection script
			collisionDetect(socket, room, thisPlayer, tempObjects);
    
			socket.emit('objectSync', tempObjects);
    
			if (thisRoom.started)
				socket.emit('time', { time: thisRoom.time.formattedTime });
    
			if (thisRoom !== undefined && !thisRoom.started) {
				// Send over the room player information
				// socket.to(room).broadcast.emit('roomInfo', thisRoom.players);
				socket.emit('roomInfo', thisRoom.players);
			}
		}
	}
}

import { GLOBAL } from '../../client/js/global';
import { getField, setField } from '../server';
import { generateID } from './serverutils';

/**
 * Matchmaking system for public matches. Runs after initial socket.io server connection, but before connecting to a server.
 * @param {*} socket Socket.io instance
 * @param {string} room Name of room
 * @param {string} team Name of team
 * @returns The new room that has been assigned
 */
export function roomMatchmaker(socket, room, team) {

	let validJoin = false; // This join attempt was valid.
	let roomType = socket.handshake.query.roomType;

	// Make sure the room you are trying to join is valid
	// console.log(getField(['rooms', room]));
	if (room !== GLOBAL.NO_ROOM_IDENTIFIER && getField(['rooms', room]) !== undefined && !getField(['rooms', room]).joinable && getField(['rooms', room]) !== undefined) // Room full
		socket.emit('connectionError', { msg: 'The room ' + room + ' has started or is full!' });

	if (team !== undefined && team.room !== undefined) {
		// Make sure everything is compatible
		if (getField(['rooms', team.room]) !== undefined && getField(['rooms', team.room]).type !== roomType) // Wrong room type
			socket.emit('connectionError', { msg: 'Your team is playing in a ' + getField('rooms', team.room).type + ' room, but you are trying to join a ' + roomType + ' room!' });
		else if (!team.joinable) // Team full
			socket.emit('connectionError', { msg: 'Your team is already in game or full!' });
		else {// is joinable
			validJoin = true;
			room = team.room;

			// Equivalent to teams[socket.handshake.query.team].players.push(socket.id);
			setField(socket.id, ['teams', socket.handshake.query.team, 'players', getField(['teams', socket.handshake.query.team, 'players']).length ]);

			if ((roomType === '2v2v2v2' && team.players.length === 2) || team.players.length === 4)
				setField(false, ['teams', socket.handshake.query.team, 'joinable']);
		}
	}
	// Team not found 
	else {
		// Try joining a room
		for (let roomName in getField(['rooms'])) {
			if (roomName.indexOf(roomType) > -1)
				if ((roomType === '4v4' && getField(['rooms', roomName, 'teams']).length < 2) || getField(['rooms', roomName, 'teams']).length < 4) {
					room = roomName;
				}
		}

		// No matching rooms - must create a new room
		if (room === GLOBAL.NO_ROOM_IDENTIFIER)
			room = 'NA_' + roomType + '_' + generateID();

		// Make team
		setField({
			room: room,
			players: [socket.id],
			joinable: true
		}, ['teams', socket.handshake.query.team]);

		validJoin = true;
	}

	// Join custom room
	if (validJoin)
		socket.join(room, () => {
			console.log('[Server] '.bold.blue + `Player ${socket.handshake.query.name} (${socket.id}) joined room ${room} in team ${socket.handshake.query.team}`.yellow);
		});

	return room;
}

import { GLOBAL } from '../../client/js/global';
import { getField, setField } from '../server';
